//generate raidx-16 verilog file 
void CSA::gen(unsigned long fft_p,unsigned long radix ,std::string string_in){
     double  stage_doub;
     double  stage_ceil;
     double  stage_floor;
	 double  radix_bits;
     double  IsMixed;
	 unsigned long  Mixed_radix;
	 stage_doub  = log2(fft_p);
	 radix_bits  = log2(radix);
	 stage_ceil  = ceil(stage_doub / radix_bits ); 
	 stage_floor = floor(stage_doub / radix_bits ); 
	 if(stage_ceil == stage_floor) IsMixed = 0;
	 else IsMixed = 1;
	 
	 
	 fft_point   = fft_p;
	 Mixed_radix = fft_point;
	 while( Mixed_radix % 16 == 0){
	     Mixed_radix = Mixed_radix / 16 ;
	 }
	 
	 r = radix;
	 
     if(r==4){
	   if(IsMixed == 0){
         Sum4_CSAout0(string_in);
         Sum4_CSAout1(string_in);
         Sum4_CSAout2(string_in);
         Sum4_CSAout3(string_in);
       }else {
         Sum4_r2_CSAout0(string_in);
         Sum4_r2_CSAout1(string_in);
         Sum4_r2_CSAout2(string_in);
         Sum4_r2_CSAout3(string_in);
       }		   
     }
	 if(r==8){
		Sum8_CSAout0(string_in); 
		Sum8_CSAout1(string_in); 
		Sum8_CSAout2(string_in); 
		Sum8_CSAout3(string_in);       
		Sum8_CSAout4(string_in);
		Sum8_CSAout5(string_in);
		Sum8_CSAout6(string_in);
		Sum8_CSAout7(string_in);      	 
	 }
	 if(r==16){
		if(IsMixed == 0){ 
            Sum16_CSAout0( string_in);
            Sum16_CSAout1( string_in);
            Sum16_CSAout2( string_in);
            Sum16_CSAout3( string_in);
            Sum16_CSAout4( string_in);
            Sum16_CSAout5( string_in);
            Sum16_CSAout6( string_in);
            Sum16_CSAout7( string_in);
            Sum16_CSAout8( string_in);
            Sum16_CSAout9( string_in);
            Sum16_CSAout10( string_in);
            Sum16_CSAout11( string_in);
            Sum16_CSAout12( string_in);
            Sum16_CSAout13( string_in);
            Sum16_CSAout14( string_in);
            Sum16_CSAout15( string_in);
		}else{
			if(Mixed_radix == 2){
				Sum16_R2_CSAout0( string_in);
				Sum16_R2_CSAout1( string_in);
				Sum16_R2_CSAout2( string_in);
				Sum16_R2_CSAout3( string_in);
				Sum16_R2_CSAout4( string_in);
				Sum16_R2_CSAout5( string_in);
				Sum16_R2_CSAout6( string_in);
				Sum16_R2_CSAout7( string_in);
				Sum16_R2_CSAout8( string_in);
				Sum16_R2_CSAout9( string_in);
				Sum16_R2_CSAout10( string_in);
				Sum16_R2_CSAout11( string_in);
				Sum16_R2_CSAout12( string_in);
				Sum16_R2_CSAout13( string_in);
				Sum16_R2_CSAout14( string_in);
				Sum16_R2_CSAout15( string_in);						
			}else if(Mixed_radix == 4){
				Sum16_R4_CSAout0( string_in);
				Sum16_R4_CSAout1( string_in);
				Sum16_R4_CSAout2( string_in);
				Sum16_R4_CSAout3( string_in);
				Sum16_R4_CSAout4( string_in);
				Sum16_R4_CSAout5( string_in);
				Sum16_R4_CSAout6( string_in);
				Sum16_R4_CSAout7( string_in);
				Sum16_R4_CSAout8( string_in);
				Sum16_R4_CSAout9( string_in);
				Sum16_R4_CSAout10( string_in);
				Sum16_R4_CSAout11( string_in);
				Sum16_R4_CSAout12( string_in);
				Sum16_R4_CSAout13( string_in);
				Sum16_R4_CSAout14( string_in);
				Sum16_R4_CSAout15( string_in);			
			}else if(Mixed_radix == 8){
				Sum16_R8_CSAout0( string_in);
				Sum16_R8_CSAout1( string_in);
				Sum16_R8_CSAout2( string_in);
				Sum16_R8_CSAout3( string_in);
				Sum16_R8_CSAout4( string_in);
				Sum16_R8_CSAout5( string_in);
				Sum16_R8_CSAout6( string_in);
				Sum16_R8_CSAout7( string_in);
				Sum16_R8_CSAout8( string_in);
				Sum16_R8_CSAout9( string_in);
				Sum16_R8_CSAout10( string_in);
				Sum16_R8_CSAout11( string_in);
				Sum16_R8_CSAout12( string_in);
				Sum16_R8_CSAout13( string_in);
				Sum16_R8_CSAout14( string_in);
				Sum16_R8_CSAout15( string_in);					
			}
		}
     }     
}

//radix-4
void CSA::Sum4_CSAout0(std::string string_in){
	 std::ofstream sum4_csaout0;
     std::string        string_buf = "/Sum4_CSAout0.v";
     std::stringstream  ss;
     ss << string_in << string_buf;
	 sum4_csaout0.open(ss.str()); 
sum4_csaout0 << " `timescale 1 ns/1 ps                                                           \n";
sum4_csaout0 << " module Sum4_CSAout0(SS_out,                                                    \n";
sum4_csaout0 << " 				    SC_out,                                                      \n";
sum4_csaout0 << " 					A0_in,                                                       \n";
sum4_csaout0 << " 					A1_in,                                                       \n";
sum4_csaout0 << " 					A2_in,                                                       \n";
sum4_csaout0 << " 					A3_in                                                        \n";
sum4_csaout0 << " 					// rst_n,                                                    \n";
sum4_csaout0 << " 					// clk                                                       \n";
sum4_csaout0 << " 					) ;                                                          \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " parameter D_WIDTH     = 96 ;                                                   \n";
sum4_csaout0 << " parameter P_WIDTH     = 64 ;                                                   \n";
sum4_csaout0 << " parameter D_ZERO      = 96'h0 ;                                                \n";
sum4_csaout0 << " parameter SEG_ZERO    = 32'h0 ;                                                \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " output[D_WIDTH-1:0] SS_out ;                                                   \n";
sum4_csaout0 << " output[D_WIDTH-1:0] SC_out ;                                                   \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " input [P_WIDTH-1:0] A0_in ;                                                    \n";
sum4_csaout0 << " input [P_WIDTH-1:0] A1_in ;                                                    \n";
sum4_csaout0 << " input [P_WIDTH-1:0] A2_in ;                                                    \n";
sum4_csaout0 << " input [P_WIDTH-1:0] A3_in ;                                                    \n";
sum4_csaout0 << " // input               rst_n ;                                                 \n";
sum4_csaout0 << " // input               clk ;                                                   \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  op0 ;                                                     \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  op1 ;                                                     \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  op2 ;                                                     \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  op3 ;                                                     \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  ts1_wire ;                                                \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  tc1_wire ;                                                \n";
sum4_csaout0 << " // wire  [D_WIDTH-1:0]  s1_wire ;                                              \n";
sum4_csaout0 << " wire  [D_WIDTH-1:0]  c1_wire ;                                                 \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " 	//                                                                           \n";
sum4_csaout0 << " 	assign op0 = {SEG_ZERO,A0_in} ;                                              \n";
sum4_csaout0 << " 	//                                                                           \n";
sum4_csaout0 << " 	assign op1 = {SEG_ZERO,A1_in} ;                                              \n";
sum4_csaout0 << " 	//                                                                           \n";
sum4_csaout0 << " 	assign op2 = {SEG_ZERO,A2_in} ;                                              \n";
sum4_csaout0 << " 	//                                                                           \n";
sum4_csaout0 << " 	assign op3 = {SEG_ZERO,A3_in} ;                                              \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	//4-2 counter1                                                               \n";
sum4_csaout0 << " 	assign ts1_wire = op0^op1^op2 ;                                              \n";
sum4_csaout0 << " 	assign tc1_wire = (op0&op1)|(op1&op2)|(op0&op2) ;                            \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	assign SS_out = ({tc1_wire[D_WIDTH-2:0],1'b0})^ts1_wire^op3 ;                \n";
sum4_csaout0 << " 	assign c1_wire = (({tc1_wire[D_WIDTH-2:0],1'b0})&ts1_wire)|                  \n";
sum4_csaout0 << "                      (ts1_wire&op3)|(({tc1_wire[D_WIDTH-2:0],1'b0})&op3) ;     \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	assign SC_out = {c1_wire[D_WIDTH-2:0],1'b0} ;                                \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	//CPA                                                                        \n";
sum4_csaout0 << " 	// assign S_out = {c1_wire[D_WIDTH-2:0],1'b0} + s1_wire ;                    \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	                                                                             \n";
sum4_csaout0 << " 	//                                                                           \n";
sum4_csaout0 << " 	// always @(posedge clk or negedge rst_n) begin                              \n";
sum4_csaout0 << " 		// if(~rst_n) begin                                                      \n";
sum4_csaout0 << " 			// S_out <= D_ZERO ;                                                 \n";
sum4_csaout0 << " 		// end                                                                   \n";
sum4_csaout0 << " 		// else begin                                                            \n";
sum4_csaout0 << " 			// S_out <= S_wire ;                                                 \n";
sum4_csaout0 << " 		// end                                                                   \n";
sum4_csaout0 << " 	// end                                                                       \n";
sum4_csaout0 << "                                                                                \n";
sum4_csaout0 << " endmodule                                                                      \n";

     sum4_csaout0.close();     
}
